export const ADD_FAVORITE = 'ADD_FAVORITE';
export const DELETE_FAVORITE = 'DELETE_FAVORITE';

export function addFavorite(payload) {
    return {
      type: ADD_FAVORITE,
      payload
    };
  }
  
  export function deleteFavorite(payload) {
    return {
      type: DELETE_FAVORITE,
      payload,
    };
  }
