import {ADD_FAVORITE, DELETE_FAVORITE, FILTER, ORDER} from '../redux/actions'

const initialState = {
    myFavorites: [],
    allCharacters: []
};

const rootReducer = (state = initialState, {type, payload}) => {
    switch (type) {
        case ADD_FAVORITE:
            return {
                ...state,
                myFavorites: [
                    ...state.myFavorites,
                    payload
                ]
            }      
        case DELETE_FAVORITE:
            // opción b:
            // const filtered = state.myFavorites.filter((fav) => fav.id !== payload)
            // y entonnces myFavorites: filtered
            return {
                ...state,
                myFavorites: state.myFavorites.filter((fav) => fav.id !== payload)
            }  
        case FILTER:
            return {
                ...allCharacters,
                myFavorites: state.filter((fav) => fav.gender !== action.payload)
            } 
            // myFavorites: [...state.myFavorites, action.payload]
        case ORDER:
            return {
                ...allCharacters,
                allCharacters: state.sort([(fav) => fav.id !== action.payload])
            }   
        default:
            return state;
    }
}


export default rootReducer;
