import {ADD_FAVORITE, DELETE_FAVORITE} from '../redux/actions'

const initialState = {
    myFavorites: []
};

const rootReducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_FAVORITE:
            return {
                ...state,
                myFavorites: [...state, payload]
            }      
        case DELETE_FAVORITE:
            return {
                ...state,
                myFavorites: state.filter((fav) => fav.id !== payload)
            }  
            
        default:
            return state;
    }
}


export default rootReducer;
